int attempt = 1;

    private void btnmasukActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnmasukActionPerformed
        try {
            String sql = " SELECT * FROM data_karyawan WHERE username ='"
                    + txtuser.getText() + "'AND password='"
                    + txtpass.getText() + "'";
            java.sql.Connection conn = (Connection) Connect.ConfigDB();
            java.sql.PreparedStatement pst = conn.prepareStatement(sql);
            java.sql.ResultSet rs = pst.executeQuery(sql);

            if (rs.next()) {
                if (rs.getString("level").equals("admin")) {            // ====== tingkatan / status akun level-admin akan masuk kedalam dashboard admin
                    new DashboardAdminPegawai().setVisible(true);       // membuka Dashboard admin 
                    new notificationBerhasil().setVisible(true);        // menampilkan notifikasi Berhasil 
                } else if (rs.getString("level").equals("pegawai")) {   // ====== Jika bukan tingkatan admin maka yang akan dieksekusi baris ini yaitu level "pegawai"
                    new DashboardPegawaiJasa().setVisible(true);        // membuka Dashboard pegawai 
                    new notificationBerhasil().setVisible(true);        // menampilkan notifikasi Berhasil 
                }
            } else {
                if (attempt <= 3) { // jika percobaan kurang dari = 3 (4x percobaan) akan di meneksekusi else 
                    JOptionPane.showMessageDialog(this, "Access Ditolak! \n Percobaan :" + attempt + "/3");
                    attempt++; // statement menambahkan 1 angka setiap percobaan 
                } else {
                    JOptionPane.showMessageDialog(this, "Program akan dihentikan! \n Silahkan reset password atau konfirmasi ke admin :D"); // statement akhir ketika telah salah 3x
                    System.exit(0);
                }
            }
        } catch (HeadlessException | SQLException e) { // menampilkan pesan eror 
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
    }//GEN-LAST:event_btnmasukActionPerformed

