    private void showMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_showMouseClicked
        // TODO add your handling code here:
        if (show.isEnabled()) {
            txtpass.setEchoChar((char) 0);
        } else {
            txtpass.setEchoChar('*');

        }
    }//GEN-LAST:event_showMouseClicked

